import React, { useState, useEffect } from 'react';
import requests from './requests';

function Banner() {
    const [movie, setMovie]=useState([]);

    useEffect(() => {
        async function fetchData(){
            const popularMovies=await axios.get(requests.fetchPopular);
            setMovie(popularMovies.data.results[3]
                );
            return popularMovies;
        }
        fetchData();
    }, [])
    return (
        <header 
        className='banner'
        style={{
            backgroundSize:'cover',
            backgroundImage:`url("https://image.tmdb.org/t/p/original${movie?.backdrop_path}")`,
            backgroundPosition:'center center',
        }}
         > 
            <div className='banner_contents'>
                <h1>Banner Contents</h1>
                <p>Banner Description.Banner Description.Banner Description.Banner Description.Banner Description.Banner Description.</p>
            </div>
         
            {/* title */}
           {/* div >2 buttons */}
            {/* description */}
        </header>
    )
}

export default Banner
